using Legendigital.Framework.Common.BaseFramework.Bussiness.SystemConst;

namespace SPS.Entity.Tables
{
	[Serializable]
	public partial class SPCodeEntity
	{
	    public virtual bool CheckIsRoot(List<SPCodeEntity> allList)
	    {
            if(this.MOType == DictionaryConst.Dictionary_CodeType_CodeEQ_Key)
                return true;
            if (this.MOType == DictionaryConst.Dictionary_CodeType_CodeStartWith_Key)
            {
                return !allList.Exists(p => (
                                                p.MOType == DictionaryConst.Dictionary_CodeType_CodeStartWith_Key
                                                && p.SPCode == this.SPCode
                                                && !p.Mo.Equals(this.Mo)
                                                && p.Mo.StartsWith(this.Mo))
                                             );

            }
	        return true;

	    }
	}
